tool.command:
echo ${genome_version}.genome >> "\$SNPEFF_JAR_PATH/snpEff.config";
mkdir -p $snpeff_db.files_path/$genome_version;
cp $genome $snpeff_db.files_path/$genome_version/sequences.fa;
cp $annotation $snpeff_db.files_path/$genome_version/genes.gff;
java -Xmx6G -jar "\$SNPEFF_JAR_PATH/snpEff.jar" build  -gff3 -v $genome_version -dataDir $snpeff_db.files_path
