tool.command:
cd-hit$subtool.tool_select -i '$fasta_in' -o rep_seq #if $subtool.tool_select == '' or $subtool.tool_select == '-2d': -c $subtool.similarity -n $subtool.wordsize -t $subtool.redtol #end if #if $subtool.tool_select == '-est': -cx $subtool.cx $subtool.posconstraint #if $subtool.paired_file.paired_file_select == "yes": -j '$fasta_j' -P 1 -cy $subtool.paired_file.cy #end if #end if #if $subtool.tool_select == '-est-2d': -cx $subtool.cx #if $subtool.paired_file2d.paired_file2d_select == "yes": -j '$fasta_j' -j2 '$fasta_j2' -P 1 -cy $subtool.paired_file2d.cy #end if #end if #if $subtool.tool_select == '-est' or $subtool.tool_select == '-est-2d': -c $subtool.similarity -n $subtool.wordsize $subtool.strand #if str($subtool.estadvalign.mask) != 'None': -mask '$subtool.estadvalign.mask' #end if -match $subtool.estadvalign.match -mismatch $subtool.estadvalign.mismatch -gap $subtool.estadvalign.gap -gap-ext $subtool.estadvalign.gapext #end if #if $subtool.tool_select == '-454': -c $subtool.similarity -n $subtool.wordsize -D $subtool.estadvalign.D -match $subtool.estadvalign.match -mismatch $subtool.estadvalign.mismatch -gap $subtool.estadvalign.gap -gap-ext $subtool.estadvalign.gapext #end if #if $subtool.tool_select == '-2d'or $subtool.tool_select == '-est-2d': -i2 '$fasta_in2' #if $advanced.advalign.style == 'local': -s2 $advanced.advalign.advancedtwod.cutoff_diff_len2 -S2 $advanced.advalign.advancedtwod.aa_cutoff_diff_len2 #end if #end if
-b $advanced.band_width -l $advanced.throw_away_len #if $advanced.advalign.style == 'local': -G 0 -aL $advanced.advalign.align_coverage_long -AL $advanced.advalign.align_coverage_long_control -aS $advanced.advalign.align_coverage_short -AS $advanced.advalign.align_coverage_short_control -A $advanced.advalign.align_coverage_min -s $advanced.advalign.cutoff_diff_len -S $advanced.advalign.aa_cutoff_diff_len #end if -uL $advanced.max_unmatched_per_l -uS $advanced.max_unmatched_per_s -U $advanced.max_unmatched_len $advanced.accurate #if $subtool.tool_select == '' or $subtool.tool_select == '-est': -sc $subtool.sort_cluster -sf $subtool.sort_fasta #end if #if $subtool.tool_select == '' or $subtool.tool_select == '-est' $subtool.tool_select == '-2d' or $subtool.tool_select == '-est-2d': #if $print_alnovl.print_alnovl_select == "yes": -p 1 -d $print_alnovl.desclen #end if #end if
## instead of 800 (default) we use 0:unlimited -M \${GALAXY_MEMORY_MB:-0} -T \${GALAXY_SLOTS:-1}
