## Import library library(csaw) library(Rsamtools)
## windowCounts repeat parameters bamFiles <- c() r <- c() count <- c() spacing <- list() shift <- list() filter <- list() binList <- list() widthList <- list() extList <- list() window_counts <- c()
#for $i, $j in enumerate($data) #for $x, $y in enumerate($j.bam_input) r <- c(r, $x) bamFiles <- c(bamFiles, "$y.bam_file") #end for
spacing <- append(spacing, list(as.numeric("$j.spacing")))
#if str($j.width) != '' width <- as.numeric("$j.width") #else width <- as.numeric("$j.spacing") #end if
widthList <- append(widthList, list(width))
#if str($j.ext_selector.ext_select) == 'integer' ext <- as.numeric("$j.ext_selector.ext") #else #if "$j.ext_selector.ext_list1" != "" ext1 <- as.numeric(unlist(strsplit("$j.ext_selector.ext_list1", ","))) #else ext1 <- NA #end if #if "$j.ext_selector.ext_list2" != "" ext2 <- as.numeric(unlist(strsplit("$j.ext_selector.ext_list2", ","))) #else ext2 <- NA #end if ext <- list(ext1, ext2) #end if
extList <- append(extList, list(ext)) shift <- append(shift, list(as.numeric($j.shift))) filter <- append(filter, list(as.numeric($j.filter)))
#if str($j.bin) == "true" bin <- TRUE #else bin <- FALSE #end if binList <- append(binList, list(bin)) #end for
len <- length(r)-1
for(i in seq(1, len)) { if(r[i] == 0){ count <- c(count, c(r[i+1]+1)) } }
if(tail(r, n=1) == 0) { count <- c(count, 1) }
bamList <- list()
if(tail(count, n=1) == 1) { len <- length(count) - 1 for (i in seq(1,len)){ bam <- bamFiles[1: count[i]] bamList <- append(bamList, list(bam)) bamLen <- length(bamFiles) bamFiles <- bamFiles[(count[i]+1):bamLen] } bamList <- append(bamList, list(bamFiles)) } else { len <- length(count) for (i in seq(1,len)){ bam <- bamFiles[1: count[i]] bamList <- append(bamList, list(bam)) bamLen <- length(bamFiles) bamFiles <- bamFiles[(count[i]+1):bamLen] } }
for (i in bamList) {
indexBam(i) }
for (i in seq(spacing)) {
window <- windowCounts(bamList[[i]], spacing=spacing[[i]], width=widthList[[i]], ext=extList[[i]], shift=shift[[i]], filter=filter[[i]], bin=binList[[i]])
window_counts <- c(window_counts, window) }
