tool.command:
## ----------------------------------------------------------------------------- ## 1) Initialize log and credentials ## ----------------------------------------------------------------------------- #if $dry_run == "true": #set $webin_id = "test-webin" #set $password = "test-password" #else #set $webin_id = "$ENA_WEBIN_USERNAME" #set $password = "$ENA_WEBIN_PASSWORD" #end if
## ----------------------------------------------------------------------------- ## 2) Create a base manifest (fields common to all submissions) ## ----------------------------------------------------------------------------- echo "Creating a base manifest (fields common to all submissions)";
## Name of the base manifest template (we append more fields later). #set $manifest_base = 'manifest_base.tab'
## Working dirs: per-sample manifests and input sequences. mkdir -p manifests; mkdir -p fasta;
## Write assembly-level fields to the base manifest. echo -e 'ASSEMBLY_TYPE\t$assembly_type' > $manifest_base; echo -e 'COVERAGE\t$coverage' >> $manifest_base; echo -e 'PROGRAM\t$assembly_program' >> $manifest_base; #if $min_gap_length: echo -e 'MINGAPLENGTH\t$min_gap_length' >> $manifest_base; #end if echo -e 'MOLECULETYPE\t$molecule_type' >> $manifest_base;
## ----------------------------------------------------------------------------- ## 3) Build per-sample manifests depending on metadata workflow ## ----------------------------------------------------------------------------- echo "Building per-sample manifests depending on metadata workflow";
#if $metadata_file_or_form.metadata_format == "file": ## --------------------------- FILE-DRIVEN WORKFLOW ------------------------ ## For each selected FASTA: ## - if uncompressed (.fasta), gzip it into ./fasta/<name>.fasta.gz ## - if already .fasta.gz, symlink it into ./fasta #import re #for $file in $metadata_file_or_form.genome_fasta: #if $file.is_of_type('fasta'): #set $full_name = $file.element_identifier + '.gz' gzip -c '$file' > './fasta/$full_name'; #else: ln -s '$file' './fasta/$file.element_identifier'; #end if #end for
## Optionally link AGP and chromosome list files if provided (one per sample). #if $metadata_file_or_form.agp_file: #for $file in $metadata_file_or_form.agp_file: ln -s '$file' './fasta/$file.element_identifier'; #end for #end if #if $metadata_file_or_form.chr_list_file: #for $file in $metadata_file_or_form.chr_list_file: #set $chr_name = $file.element_identifier + '.gz' gzip -c '$file' > './fasta/$chr_name'; #end for #end if
## Helper script: ## - parses ENA receipt (study/sample accessions, platform), ## - writes per-sample manifests into ./manifests using $manifest_base, ## - emits "submit_list.tab" (one manifest path per line). python3 '$__tool_directory__/process_input.py' $metadata_file_or_form.ena_receipt $genome_fasta_files './manifests' $manifest_base >> "$webin_cli_log" 2>&1;
## Extract center name from the receipt (used as -centerName). center_name=`grep 'center_name' $metadata_file_or_form.ena_receipt | cut -f2,2 | tr -d '\n'`;
## Log if submit_list.tab exists, and dump its content for debugging. if [ -s submit_list.tab ]; then echo "submit_list.tab present:" >> "$webin_cli_log" 2>&1; cat submit_list.tab >> "$webin_cli_log" 2>&1; else echo "submit_list.tab is missing or empty" >> "$webin_cli_log" 2>&1; fi;
#else: ## --------------------------- FORM-DRIVEN WORKFLOW ------------------------ ## Single-manifest flow: copy base, then append form fields. #set $generated_manifest='./manifests/generated_manifest.txt' cp $manifest_base $generated_manifest;
## Use local Cheetah vars for readability. #set $study_id = $metadata_file_or_form.study_accession #set $sample_id = $metadata_file_or_form.sample_accession
## Required accessions. echo -e 'STUDY\t$study_id' >> $generated_manifest; echo -e 'SAMPLE\t$sample_id' >> $generated_manifest;
## center_name is given by the user in this path. center_name='$metadata_file_or_form.center_name';
## Assembly name and platform. echo -e 'NAME\t$metadata_file_or_form.assembly_name' >> $generated_manifest; echo -e 'PLATFORM\t$metadata_file_or_form.sequencing_platform' >> $generated_manifest;
## Normalize FASTA name referenced by the manifest. #if $metadata_file_or_form.genome_fasta.is_of_type('fasta'): #set $fasta_file_name = $metadata_file_or_form.genome_fasta.element_identifier + '.gz' gzip -c '$metadata_file_or_form.genome_fasta' > $fasta_file_name; #else: #set $fasta_file_name = $metadata_file_or_form.genome_fasta.element_identifier #end if echo -e 'FASTA\t$fasta_file_name' >> $generated_manifest;
## Optional extras for chromosome-scale assemblies. #if $metadata_file_or_form.agp_file: echo -e 'AGP\t$metadata_file_or_form.agp_file.element_identifier' >> $generated_manifest; #end if #if $metadata_file_or_form.chr_list_file: ## If the name ends with .tsv, gzip it and reference the .gz; else use as-is. #set $chr_file_name = $metadata_file_or_form.chr_list_file.element_identifier + '.gz' gzip -c '$metadata_file_or_form.chr_list_file' > $chr_file_name; echo -e 'CHROMOSOME_LIST\t$chr_file_name' >> $generated_manifest; #end if #end if
## ----------------------------------------------------------------------------- ## 4) Prepare output directory and build CLI flags ## ----------------------------------------------------------------------------- echo "Preparing output directory and build CLI flags";
## Webin-CLI will write receipts/logs under this directory (we later tar it). #set $outputs_dir = 'outputs' mkdir -p "$outputs_dir";
## Build flags using #set (safer than inline #if within a single shell line). #set $test_flag = '' #if $submit_test == "true": #set $test_flag = ' -test' #end if
## By default we submit; in dry_run we validate instead. #set $action_flag = ' -submit' #if $dry_run == "true": #set $action_flag = ' -validate' #end if
## ----------------------------------------------------------------------------- ## 5) Execute Webin-CLI ## ----------------------------------------------------------------------------- echo "Executing Webin-CLI";
#if $metadata_file_or_form.metadata_format == "file": ## Loop over each manifest written by process_input.py (submit_list.tab). while IFS= read -r line; do ## Extract the manifest path (first whitespace-delimited field). manifest=`echo "\$line" | cut -d' ' -f1`;
## Log which manifest we are submitting. echo "Submitting manifest \$manifest" >> "$webin_cli_log" 2>&1;
## Invoke Webin-CLI with computed flags. ena-webin-cli -context genome -manifest "\$manifest" -userName "$webin_id" -password "$password" -centerName "\$center_name" -inputDir "./fasta" $test_flag $action_flag -outputDir $outputs_dir >> '$webin_cli_log' 2>&1 || true; done < submit_list.tab;
#else: ## Single run in "form" mode with the one generated manifest. ena-webin-cli -context genome -manifest $generated_manifest -userName "$webin_id" -password "$password" -centerName "\$center_name" -inputDir "./" $test_flag $action_flag -outputDir $outputs_dir >> "$webin_cli_log" 2>&1 || true; #end if
## ----------------------------------------------------------------------------- ## 6) Package outputs for Galaxy ## ----------------------------------------------------------------------------- echo "Packaging outputs for Galaxy";
## Tar up the Webin-CLI output directory so Galaxy can collect a single dataset. tar -cf $webin_cli_outputs $outputs_dir ;
