configfiles.configfile:
@R_INIT@
## Import library library(reshape2) library(ggplot2)
integrated <- read.csv('$input1', sep='\t', header=TRUE) input <- melt(integrated)
@LEGEND@
## density #if $adv.density == "counts" gg_density = ggplot(input,aes(value, color=variable)) gg_freq = NULL #elif $adv.density == "nfreq" gg_density = ggplot(input,aes(value, ..ncount.., color=variable)) gg_freq = NULL #elif $adv.density == "freq" gg_density = ggplot(input,aes(value, color=variable)) gg_freq = aes(y=..count../sum(..count..)) #end if
@TRANSFORM@
@XY_SCALING@
@THEME@
#if $adv.facet == "facet" gg_facet = facet_wrap(~ variable) #else gg_facet = NULL #end if
#if $adv.coloring.colorscheme == "Default" gg_colorscale = NULL #else gg_colorscale = scale_color_brewer(palette=$adv.coloring.colors, direction=$adv.coloring.colororder) #end if
plot_out <- gg_density + geom_freqpoly(gg_freq, binwidth=$binwidth, size=$size) + gg_facet + gg_colorscale + gg_scalex + gg_scaley + gg_theme + xlab('$xlab') + ylab('$ylab') + gg_legend + ggtitle('$title') + theme(plot.title = element_text(hjust = 0.5))
@SAVE_OUTPUT@
