tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$fasta' fasta.dat && ln -s '$name' name.dat && ln -s '$count' count.dat && ln -s '$map' map.dat &&
echo 'align.check( fasta=fasta.dat, #if $name: name=name.dat, #end if #if $count: count=count.dat, #end if map=map.dat )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log && cat fasta.align.check
