tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$fasta' fasta.dat && ln -s '$name' name.dat && ln -s '$group' group.dat && ln -s '$count' count.dat &&
echo 'chop.seqs( fasta=fasta.dat, numbases=$numbases, keep=$keep, countgaps=$countgaps, short=$short, #if $name: name=name.dat, #end if #if $group: group=group.dat, #end if #if $count: count=count.dat, #end if processors='\${GALAXY_SLOTS:-8}' )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
