tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$tree' tree.dat && ln -s '$name' name.dat && ln -s '$group' group.dat && ln -s '$taxonomy' taxonomy.dat &&
echo 'classify.tree( tree=tree.dat, #if $name name=name.dat, #end if #if $group: group=group.dat, #end if taxonomy=taxonomy.dat )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
