tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$otu' otu.dat &&
echo 'collect.shared( shared=otu.dat, #if $label: label=${ str($label).replace(",","-") }, #end if #if $calc: calc=${ str($calc).replace(",","-") }, #end if #if $groups: groups=${ str($groups).replace(",","-") }, #end if freq=$freq, all=true )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log && rm otu.dat ## don't want this collected into datacollection
