echo 'collect.single( #if $otu.is_of_type("mothur.rabund"): rabund=otu.dat, #elif $otu.is_of_type("mothur.sabund"): sabund=otu.dat, #elif $otu.is_of_type("mothur.shared"): shared=otu.dat, #elif $otu.is_of_type("mothur.list"): list=otu.dat, #end if #if $label: label=${ str($label).replace(",","-") }, #end if calc=${ str($calc).replace(",","-") }, abund=$abund, freq=$freq )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log && rm otu.dat ## don't want this collected into datacollection
