tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$fasta' fasta.dat && ln -s '$name' name.dat && ln -s '$count' count.dat && #if $perotu.use == "yes": ln -s '$perotu.otu' perotu.otu.dat && #end if
echo 'consensus.seqs( fasta=fasta.dat #if $name: ,name=name.dat #end if #if $cutoff: ,cutoff=$cutoff #end if #if $perotu.use == "yes": ,list=perotu.otu.dat #if $perotu.label: ,label=${ str($perotu.label).replace(",","-") } #end if #end if #if $count: ,count=count.dat #end if )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
