tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$otu' otu.dat && ln -s '$repfasta' repfasta.dat && ln -s '$nameOrCount' nameOrCount.dat && ln -s '$constaxonomy' constaxonomy.dat && ln -s '$group' group.dat &&
echo 'create.database( #if $otu.is_of_type("mothur.list"): list=otu.dat #elif $otu.is_of_type("mothur.shared"): shared=otu.dat #end if ,repfasta=repfasta.dat #if $nameOrCount.is_of_type("mothur.names"): ,repname=nameOrCount.dat #elif $nameOrCount.is_of_type("mothur.count_table"): ,count=nameOrCount.dat #end if ,constaxonomy=constaxonomy.dat #if $group: ,group=group.dat #end if #if $label: ,label=$label #end if )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
