echo 'filter.shared( shared=otu.dat, #if $label: label=${ str($label).replace(",","-") }, #end if #if $label: groups=${ str($groups).replace(",","-") }, #end if minabund=$minabund, minpercent=$minpercent, rarepercent=$rarepercent, keepties=$keepties, minnumsamples=$minnumsamples, minpercentsamples=$minpercentsamples, mintotal=$mintotal, makerare=$makerare )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
