tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$dist' dist.dat && ln -s '$design' design.dat &&
echo 'homova( phylip=dist.dat, design=design.dat, #if $sets: sets=${ str($sets).replace(",","-") }, #end if iters=$iters, alpha=$alpha )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
