## create symlinks to input datasets ln -s '$otu' otu.dat && ln -s '$constaxonomy' constaxonomy.dat && ln -s '$metadata' metadata.dat && #if $picrustc.use == "yes": ln -s '$picrustc.picrust' picrustc.picrust.dat && ln -s '$picrustc.reftax' picrustc.reftax.dat && #end if
echo 'make.biom( shared=otu.dat, #if $constaxonomy: constaxonomy=constaxonomy.dat, #end if #if $label: label=${ str($label).replace(",","-") }, #end if #if $groups: groups=${ str($groups).replace(",","-") }, #end if #if $metadata: metadata=metadata.dat, #end if #if $picrustc.use == "yes": picrust=picrustc.picrust.dat, reftaxonomy=picrustc.reftax.dat, #end if matrixtype=$matrixtype )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
