tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$fasta' fasta.dat &&
echo 'pairwise.seqs( fasta=fasta.dat, align=$align, #if $calc: calc=$calc, #end if $countends #if float($cutoff) > 0.0: cutoff=$cutoff, #end if #if $scoring.setby == "user": match=$scoring.match, mismatch=$scoring.mismatch, gapopen=$scoring.gapopen, gapextend=$scoring.gapextend, #end if #if $output: output=$output, #end if processors='\${GALAXY_SLOTS:-8}' )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
