tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$fasta' fasta.dat && ln -s '$otu' otu.dat && ln -s '$name' name.dat && ln -s '$count' count.dat &&
echo 'primer.design( fasta=fasta.dat, list=otu.dat, #if $label: label=$label, #end if otulabel=$otulabel, #if $name: name=name.dat, #end if #if $count: count=count.dat, #end if #if $cutoff: cutoff=$cutoff, #end if #if $mintm: mintm=$mintm, #end if #if $maxtm: maxtm=$maxtm, #end if pdiffs=$pdiffs, length=$length, processors='\${GALAXY_SLOTS:-8}' )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
