tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$infile.otu' infile.otu.dat && ln -s '$accnos' accnos.dat &&
echo 'remove.otus( #if $infile.otu.is_of_type("mothur.cons.taxonomy"): constaxonomy=infile.otu.data, #end if #if $infile.otu.is_of_type("mothur.otu.corr"): otucorr=infile.otu.dat, #end if #if $infile.otu.is_of_type("mothur.axes"): corraxes=infile.otu.dat, #end if #if $infile.otu.is_of_type("mothur.list"): list=infile.otu.dat, #end if #if $infile.otu.is_of_type("mothur.shared"): shared=infile.otu.dat, #end if #if $infile.intype == "in_shared" and $infile.label: label=${ str($infile.label).replace(",","-") }, #end if accnos=accnos.dat )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
