tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s "$fasta_in" fasta_in.dat && ln -s "$names_in" names_in.dat && ln -s "$groups_in" groups_in.dat && ln -s "$qfile_in" qfile_in.dat && ln -s "$count_in" count_in.dat && ln -s "$alignreport_in" alignreport_in.dat && ln -s "$taxonomy_in" taxonomy_in.dat && ln -s "$summary" summary.dat && ln -s "$contigsreport" contigsreport.dat &&
echo 'screen.seqs( fasta=fasta_in.dat #if int($start) > -1: ,start=$start #end if #if int($end) > -1: ,end=$end #end if #if int($minlength) > -1: ,minlength=$minlength #end if #if int($maxlength) > -1: ,maxlength=$maxlength #end if #if int($maxambig) > -1: ,maxambig=$maxambig #end if #if int($maxhomop) > -1: ,maxhomop=$maxhomop #end if #if int($criteria) > -1: ,criteria=$criteria #end if #if $optimize: ,optimize=$optimize #end if #if $qfile_in: ,qfile=qfile_in.dat #end if #if $names_in: ,name=names_in.dat #end if #if $groups_in: ,group=groups_in.dat #end if #if $alignreport_in: ,alignreport=alignreport_in.dat #end if #if $taxonomy_in: ,taxonomy=taxonomy_in.dat #end if #if $count_in: ,count=count_in.dat #end if #if $summary: ,summary=summary.dat #end if #if $contigsreport: ,contigsreport=contigsreport.dat #end if ,processors='"\${GALAXY_SLOTS:-8}"' )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
