tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$sff' sff.dat && ln -s '$accnos' accnos.dat && #if $oligo.add == "yes": ln -s '$oligo.oligos' oligo.oligos.dat && #end if
echo 'sffinfo( sff=sff.dat, fasta=$fasta, qfile=$qfile, $sfftxt flow=$flow, trim=$trim #if $accnos: ,accnos=accnos.dat #end if #if $oligo.add == "yes": ,oligos=oligo.oligos.dat ,bdiffs=$oligo.bdiffs ,pdiffs=$oligo.pdiffs ,tdiffs=$oligo.tdiffs ,ldiffs=$oligo.ldiffs ,sdiffs=$oligo.sdiffs #end if )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
