tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$name' name.dat && ln -s '$count' count.dat && ln -s '$fasta' fasta.dat && ln -s '$group' group.dat &&
echo 'split.groups( #if $name: name=name.dat, #end if #if $groups: groups=${ str($groups).replace(",","-") }, #end if #if $count: count=count.dat, #end if fasta=fasta.dat, group=group.dat )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log
