tool.command:
@SHELL_OPTIONS@
## create symlinks to input datasets ln -s '$otu' otu.dat &&
echo 'summary.single( #if $otu.is_of_type("mothur.rabund"): rabund=otu.dat #elif $otu.is_of_type("mothur.sabund"): sabund=otu.dat #elif $otu.is_of_type("mothur.list"): list=otu.dat #elif $otu.is_of_type("mothur.shared"): shared=otu.dat, groupmode=$groupmode #end if #if $label: ,label=${ str($label).replace(",","-") } #end if #if $calc: ,calc=${ str($calc).replace(",","-") } #end if #if $abund: ,abund=$abund #end if #if $size: ,size=$size #end if #if $subsample.use == 'yes': #if $subsample.subsample: ,subsample=$subsample.subsample #else ,subsample=T #end if #if $subsample.iters: ,iters=$subsample.iters #end if #end if )' | sed 's/ //g' ## mothur trips over whitespace | mothur | tee mothur.out.log #if $subsample.use == 'yes' and not ($otu.extension == 'mothur.shared' and not $groupmode): && mv otu.*ave_std.summary otu.ave-std #end if
