tool.command:
echo "align.check(
    fasta="$fasta",
    #if $name:
        name="$name",
    #end if
    #if $count:
        count="$count",
    #end if
    map="$map"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$fasta";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}.align.check "$out_file";
