tool.command:
echo "chop.seqs(
    fasta="$fasta",
    numbases="$numbases",
    keep="$keep",
    countgaps="$countgaps",
    short="$short",
    #if $name:
        name="$name",
    #end if
    #if $group:
        group="$group",
    #end if
    #if $count:
        count="$count",
    #end if
    processors=\${GALAXY_SLOTS:-8}
    )" 
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$fasta";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.chop.fasta "$out_fasta";
