tool.command:
echo "classify.tree(
    tree=$tree,
    #if $name
        name=$name,
    #end if
    #if $group:
        group=$group,
    #end if
    taxonomy=$taxonomy
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$tree";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*taxonomy.tre "$out_tree";
mv \${prefix%.dat}*taxonomy.summary "$summary";
