tool.command:
echo "cluster.fragments(
    fasta="$fasta",
    diffs="$diffs",
    percent="$percent",
    #if $count:
        count="$count",
    #end if
    name="$names"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$fasta";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.fragclust.fasta "$out_fasta";
mv \${prefix%.dat}*.fragclust.names "$out_names";
