tool.command:
echo "count.seqs(
    name="$name",
    #if $grouping.use:
        group="$grouping.group",
        #if $grouping.groups:
            groups="$grouping.groups",
        #end if
    #end if
    large="$large"
    )" 
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$name";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.count_table "$seq_count";
