tool.command:
echo "get.otulist(
    list="$otu",
    #if $label:
        #set labelstring=str($label).replace(',','-')
        label=$labelstring,
    #end if
    sort="$sort"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$otu";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.otu .;
