tool.command:
echo "mantel(
     phylip1="$dist",
     phylip2="$dist2",
     method="$method",
     iters="$iters"
     )"
 | sed 's/ //g'  ## mothur trips over whitespace
 | mothur;

 ## move output files to correct destination
 prefix="$dist";
 mv mothur.*.logfile "$logfile";
 mv \${prefix%.dat}*.mantel "$mantel";
