tool.command:
#if $label:
    label="$label"
#end if
echo "parse.list(
    list="$otu",
    #if $label:
        label=\${label//,/-},
    #end if
    #if $count:
        count="$count",
    #end if
    group="$group"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$otu";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.list .;
tree;
