tool.command:
echo "pcoa(
    phylip="$phylip",
    metric="$metric"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$phylip";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.pcoa.axes "$pcoa";
mv \${prefix%.dat}*.pcoa.loadings "$loadings";
