tool.command:
echo "split.groups(
    #if $name:
        name="$name",
    #end if
    #if $groups:
        #set groupstring=str($groups).replace(',','-')
        groups="$groupstring",
    #end if
    #if $count:
        count="$count",
    #end if
    fasta="$fasta",
    group="$group"
    )"
| sed 's/ //g'  ## mothur trips over whitespace
| mothur;

## move output files to correct destination
prefix="$fasta";
mv mothur.*.logfile "$logfile";
mv \${prefix%.dat}*.fasta .;
