configfiles.configfile:
import os
import json
import argparse

parser = argparse.ArgumentParser(description="Create credentials.json for EGA access")
parser.add_argument(
    "--test",
    action="store_true",
)
args = parser.parse_args()

if args.test:
    credentials = {
        "username": "ega-test-data@ebi.ac.uk",
        "password": "egarocks"
    }
else:
    username = os.environ.get("EGA_USERNAME")
    password = os.environ.get("EGA_PASSWORD")

    credentials = {
        "username": username,
        "password": password
    }

with open("credentials.json", "w") as f:
    json.dump(credentials, f, indent=2)
