tool.expression:
{
        var name = $job.input.name;
        var pat = $job.pattern;
        var matched = false;

        try {
            var re = new RegExp(pat);
            matched = re.test(name);
        } catch (e) {
            return { "__error_message": "Invalid regular expression: " + (e && e.message ? e.message : String(e)) };
        }

        return { "matched": matched, "unmatched": !matched };
    }
