tool.command:
ln -s '$infile' ./infile.${infile.ext} &&
vg deconstruct #if $path: --path '${','.join([$p for $p in $path])}' #end if #if $path_prefix --path-prefix '${','.join([$p for $p in $path_prefix])}' #end if #if $alt_prefix --alt-prefix '${','.join([$p for $p in $alt_prefix])}' #end if #if $snarls: --snarls '$snarls' #end if $path_traversals ## -e --threads=\${GALAXY_SLOTS:-1} ./infile.${infile.ext}
> '$output'
